package com.kinztech.cache.sql.schema

import org.jetbrains.exposed.sql.Table

object Items: Table() {
    val id = integer("id").primaryKey()
    val name = varchar("name", length=255).default("")
    val examine = varchar("examine", length=255).default("").nullable()
    val resizeX = integer("resize_x").default(128)
    val resizeY = integer("resize_y").default(128)
    val resizeZ = integer("resize_z").default(128)
    val xan2d = integer("xan_2d").default(0)
    val yan2d = integer("yan_2d").default(0)
    val zan2d = integer("zan_2d").default(0)
    val xOffset2d = integer("xoff_2d").default(0)
    val yOffset2d = integer("yoff_2d").default(0)
    val zoom2d = integer("zoom_2d").default(200000)
    val cost = integer("cost").default(1)
    val weight = double("weight").default(1.0)
    val tradeable = bool("tradeable").default(false)
    val stackable = bool("stackable").default(false)
    val model = integer("model").default(0)
    val members = bool("members").default(false)
    val colorFind = text("color_find").nullable()
    val colorReplace = text("color_replace").nullable()
    val textureFind = text("texture_find").nullable()
    val textureReplace = text("texture_replace").nullable()
    val ambient = integer("ambient").default(0)
    val contrast = integer("contrast").default(0)
    val countCo = text("count_co").nullable()
    val countObj = text("count_obj").nullable()
    val groundActions = varchar("ground_actions", length=255).default("['','', 'Take', '', '']")
    val interfaceActions = varchar("interface_actions", length=255).default("['','', '', '', 'Drop']")
    val maleModel0 = integer("male_model_0").default(0)
    val maleModel1 = integer("male_model_1").default(0)
    val maleModel2 = integer("male_model_2").default(0)
    val maleOffset = integer("male_offset").default(0)
    val maleHeadModel0 = integer("male_head_0").default(0)
    val maleHeadModel1 = integer("male_head_1").default(0)
    val femaleModel0 = integer("female_model_0").default(0)
    val femaleModel1 = integer("female_model_1").default(0)
    val femaleModel2 = integer("female_model_2").default(0)
    val femaleOffset = integer("female_offset").default(0)
    val femaleHeadModel0 = integer("female_head_0").default(0)
    val femaleHeadModel1 = integer("female_head_1").default(0)
    val notedId = integer("noted_id").default(0)
    val notedTemplate = integer("noted_template").default(0)
    val team = integer("team").default(0)
    val shiftClickDropIndex = integer("shift_drop_index").default(-2)
    val boughtId = integer("bought_id").default(0)
    val boughtTemplate = integer("bought_template").default(0)
    val placeholderId = integer("placeholder_id").default(0)
    val placeholderTemplate = integer("placeholder_template").default(0)
    val params = text("params").nullable()
}